// putting into 1 function like this, rather than individual variables
// reduces ram usage from 70%-13%
function getGaugeImage(p) {
  // p0
  if (p < 2) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p2
  if (p >= 2 && p < 4) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette: pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p4
  if (p >= 4 && p < 7) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p7
  if (p >= 7 && p < 10) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p10
  if (p >= 10 && p < 20) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p20
  if (p >= 20 && p < 30) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p30
  if (p >= 30 && p < 40) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p40
  if (p >= 40 && p < 50) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal1,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p50
  if (p >= 50 && p < 60) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal2,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p60
  if (p >= 60 && p < 70) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal2,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p70
  if (p >= 70 && p < 80) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal2,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p80
  if (p >= 80 && p < 90) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal2,
    buffer : require("heatshrink").decompress(atob(""))
  };
  
  // p90
  if (p >= 90 && p < 100) return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal2,
    buffer : require("heatshrink").decompress(atob(""))
  };

  // p100
  return {
    width : 176, height : 176, bpp : 2,
    transparent : -1,
    palette : pal2,
    buffer : require("heatshrink").decompress(atob(""))
  };
}

